import styled from 'styled-components';

const SmoothCorner = styled.button`
  display: flex;
  justify-content: center;
  align-items: center;
  width: ${props => (props.width ? props.width : '5rem')};
  height: ${props => (props.height ? props.height : '5rem')};
  background-color: ${props =>
    props.backgroundColor ? props.backgroundColor : 'var(--ui-03)'};
  z-index: 20;

  border-radius: 1rem;
  @supports (mask-image: paint(smooth-corners)) {
    & {
      border-radius: 0;
      --smooth-corners: ${props => (props.amount ? props.amount : '3')};
      mask-image: paint(smooth-corners);
    }
  }
`;

export default SmoothCorner;
